// SPDX-License-Identifier: MIT
pragma solidity ^0.8.11.0;

import "../node_modules/@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "../node_modules/@openzeppelin/contracts/access/Ownable.sol";
import "../node_modules/@openzeppelin/contracts/security/ReentrancyGuard.sol";

interface IExchangeCurrency {
    function balanceOf(address account) external view returns (uint256);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
    function decimals() external view returns (uint8);
}

error LowExchangeCurrencyBalance();
error InvalidExchangeCurrency();
error MintToZeroAddress();
error RedeemToZeroAddress();
error MintZeroQuantity();
error AddingOwnAddress();
error LowBalanceInContract();
error InsufficientBalanceToRedeem();

contract INRCToken is ERC20, Ownable, ReentrancyGuard {
    IExchangeCurrency private ExchangeCurrency;
    mapping(string => address) public exchangeCurrencies;

    constructor() ERC20("INRCToken", "INRC") {}

    function mint(address to, uint256 amount) public onlyOwner {
        _mint(to, amount);
    }

    /** 
     * Adds a new exchange currency(e.g. DUSDC, 0x12D3424BdC7a9FC2f6F9D794810641662967a412)
     */
    function addExchangeCurrency(string memory exchangeCurrencyName, address exchangeCurrencyAddress) public onlyOwner {
        if (exchangeCurrencyAddress == address(this)) {
            revert AddingOwnAddress();
        }
        exchangeCurrencies[exchangeCurrencyName] = exchangeCurrencyAddress;
    }

    function buy(string memory exchangeCurrencyName, uint256 amount) public {
        address receiver = msg.sender;
        if (amount == 0) {
            revert MintZeroQuantity();
        }

        if (msg.sender == address(0)) {
            revert MintToZeroAddress();
        }

        uint256 amountWithoutDecimals = amount;
        address exchangeCurrencyAddress = exchangeCurrencies[exchangeCurrencyName];
        if(exchangeCurrencyAddress == address(0x0)) {
            revert InvalidExchangeCurrency();
        }
        ExchangeCurrency = IExchangeCurrency(exchangeCurrencyAddress);
        uint256 exchangeCurrencyBalance = ExchangeCurrency.balanceOf(receiver);

        // This is added so that there is no mistake from caller end in putting decimals
        uint8 exchangeCurrencyDecimals = ExchangeCurrency.decimals();

        amount = amount * (10 ** exchangeCurrencyDecimals);
        uint256 transactionFee = amount * 5 / 1000;
        amount = amount + transactionFee;

        if(exchangeCurrencyBalance < amount) {
            revert LowExchangeCurrencyBalance();
        }

        ExchangeCurrency.transferFrom(receiver, address(this), amount);

        // The conversion rate of 1:80 can be kept in mappings if more currencies are added
        uint256 tokensToMint = amountWithoutDecimals * 80 * (10 ** 18);
        _mint(receiver, tokensToMint);
    }

    function redeem(string memory exchangeCurrencyName, uint256 amount) public {
        address receiver = msg.sender;
        if (amount == 0) {
            revert MintZeroQuantity();
        }

        if (msg.sender == address(0)) {
            revert RedeemToZeroAddress();
        }

        address exchangeCurrencyAddress = exchangeCurrencies[exchangeCurrencyName];
        if(exchangeCurrencyAddress == address(0x0)) {
            revert InvalidExchangeCurrency();
        }
        ExchangeCurrency = IExchangeCurrency(exchangeCurrencyAddress);

        // Checking exchange currency(USDC) balance of contract
        uint256 contractExchangeCurrencyBalance = ExchangeCurrency.balanceOf(address(this));
        uint256 exchangeCurrencyAmount = amount * (10 ** ExchangeCurrency.decimals());

        if(exchangeCurrencyAmount > contractExchangeCurrencyBalance) {
            revert LowBalanceInContract();
        }

        // Calculating amount to charge in INRC with transaction fee + conversion ratio
        amount = amount * 80 * (10 ** 18);
        uint256 transactionFee = amount * 5 / 1000;
        amount += transactionFee;

        // Checking INRC balance of user and transferring it back to contract if available
        // Transfer from receiver to contract will require the user to approve the contract
        if(this.balanceOf(receiver) < amount) {
            revert InsufficientBalanceToRedeem();
        }
        this.transferFrom(receiver, address(this), amount);

        // Transfering exchange currency(USDC) back to user
        ExchangeCurrency.transferFrom(address(this), receiver, exchangeCurrencyAmount);
    }
}
